mapper.insert:
drop temporary table if exists temp_project_billing; create temporary table temp_project_billing ( project_id varchar(32), project_name varchar(100), implement_commission decimal(20, 4), leader_id varchar(32), leader_name varchar(100) ); insert into temp_project_billing(project_id, project_name,implement_commission, leader_id, leader_name) values
(#{data.projectId},#{data.projectName},#{implementCommission},#{data.leaderId},#{data.leaderName})
; INSERT INTO biz_project_billing (id, project_id, project_name, implement_commission, leader_id, leader_name, billing_status, del_flag, create_by, created_time, update_by, update_time) select replace(uuid(), '-', ''), project_id, project_name, implement_commission, leader_id, leader_name, 1, 0, null, now(), null, now() from temp_project_billing tep where not exists ( select 1 from biz_project_billing bpcc where bpcc.project_id = tep.project_id and bpcc.del_flag=0 ); drop temporary table if exists temp_project_billing;
